mapper.select:
SELECT * FROM ADMINISTRATOR ad JOIN EMPLOYEE e ON ad.employee_ID = e.employee_id WHERE 1=1
AND e.name = #{name}
AND TO_CHAR(TO_DATE(e.birth, 'YYYY-MM-DD'), 'YYMMDD') = #{birthDay}
AND e.gender = '남성'
AND e.gender = '여성'

SELECT * FROM EMPLOYEE
WHERE 1 = 1 

	AND employee_id = #{searchKeyword}


	AND jobs_id = #{jobsId}


	AND name LIKE '%' || #{searchKeyword} || '%'   


	AND phone LIKE '%' || #{searchKeyword} || '%'   


	AND tel LIKE '%' || #{searchKeyword} || '%'   


	AND birth BETWEEN #{birthStartDate} AND #{birthEndDate}  


	AND gender = #{gender}


	AND address = #{address}


	AND hire_date BETWEEN #{hireStartDate} AND #{hireEndDate}  


	AND email LIKE '%' || #{searchKeyword} || '%'

SELECT * FROM EMPLOYEE WHERE 1=1
AND name = #{name}
AND TO_CHAR(TO_DATE(birth, 'YYYY-MM-DD'), 'YYMMDD') = #{birthDay}
AND gender = '남성'
AND gender = '여성'

SELECT * FROM administrator_request WHERE 1=1
AND name = #{name}
AND TO_CHAR(TO_DATE(birth, 'YYYY-MM-DD'), 'YYMMDD') = #{birthDay}
AND gender = '남성'
AND gender = '여성'
